@section('js')
<script>
    $('#btn-start').click(function() {
        $('html, body').animate({
            scrollTop: $('#main').offset().top - $('.nav').height()
        }, 500);
    });

    $('#nav-btn-sign-in').click(function() {
        $('html, body').animate({
            scrollTop: $('#index__bottom').offset().top - $('.nav').height()
        }, 1000);
    });

    $('.icon-social-media').click(function() {
        window.open($(this).attr('data-social-href'), '_blank');
    });

    $('form').submit(function(e) {
        e.preventDefault();
        if(!$(this).find('input[type=email]').val()) {
            toastr.error('Please enter your email!');
            return;
        }

        $.ajax({
                type:'POST',
                url: "{{ route('subscription.store') }}",
                data: {
                    email: $(this).find('input[type=email]').val()
                },
                success: (data) => {
                    let { successMsg } = data;
                    toastr.success(successMsg);
                },
                error: function(error) {
                    if(error.responseJSON.errors) {
                        toastr.error(error.responseJSON.errors.email[0]);
                    }
                    if(error.errorMsg) {
                        toastr.error(error.errorMsg);
                    }
                }
            });
    });
</script>
@endsection
